import React from 'react'
import styled from 'styled-components';

export default function Bottom({stats, dob}) {

    function dateFormatter(str){
        var date = new Date(str);
        var month = date.getMonth() + 1;
        var day = date.getDate();
        var year = date.getFullYear();
        return month + "/" + day + "/" + year;
    }

    return (
        <BottomContainer>
            <AllStats>
                <StatsContainer>
                    <Stats><p>Time Alive..</p></Stats>
                    <MainStats>{dob}</MainStats>
                </StatsContainer>
                <StatsContainer>
                    <Stats><p>Technology..</p></Stats>
                    <MainStats>{stats.technology}</MainStats>
                </StatsContainer>
                <StatsContainer>
                    <Stats><p>Strength..</p></Stats>
                    <MainStats>{stats.strength}</MainStats>
                </StatsContainer>
                <StatsContainer>
                    <Stats><p>Special Powers...</p></Stats>
                    <MainStats>{stats.special_powers}</MainStats>
                </StatsContainer>
                <StatsContainer>
                    <Stats><p>Loyalty..</p></Stats>
                    <MainStats>{stats.loyalty}</MainStats>
                </StatsContainer>
            </AllStats>
            <TextContainer>
                lorem ipsum dolor sit amet consectetur adipisicing elit.
            </TextContainer>
        </BottomContainer>
    )
}

const BottomContainer = styled.div`
    height: 50%;
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;

`
const Stats = styled.div`
    flex: 3;
    background-color: rgba(0,0,0,0.5);
    clip-path: polygon(0% 0%, 100% 0%, 90% 100%, 0% 100%);
    font-size: .7em;
    height: 100%;
    // clip-path: polygon(100% 0%, 100% 100%, 10% 100%, 0% 0%);
`
const MainStats = styled(Stats)`
    flex: 1;
`

const StatsContainer = styled.div`
    display: flex;
    flex: 1;
    padding: 3px;
`
const AllStats = styled.div`
    flex: 1.5;
    dipaly: flex;
`

const TextContainer = styled.p`
    flex: 0.9;
    background-color: rgba(100,0,0,0.5);
    border-radius: 5px;
    justify-self: stretch;
`
